#include "libft.h"
#include <stdio.h>

int main() 
{
	t_vector v;
	if (!vect_init(VEC_BUFFER_SIZE, &v))
		return (1);
	printf("[%s]\n", v.buff);
	if (!vect_cat(&v, "123"))
		return (1);
	printf("[%s]\n", v.buff);
	if (!vect_cat(&v, "456"))
		return (1);
	if (!vect_cat(&v, "1231231231231231231231231312312312312312312312312312312312313123123123123"))
		return (1);
	if (!vect_cat(&v, ""))
		return (1);
	if (!vect_cat(&v, "**********************************"))
		return (1);
	printf("[%s]\n", v.buff);
	return 0;
}
